import React, { useState } from 'react';


class CustomMenu extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      menuIndex: 0, // Default menu index
      isOpen: false // State to control the sidenav visibility
    };
  }

  // Different menu configurations based on the menuIndex
  menuConfigs = [
    [
      { type: 'slider', label: 'Volume', min: 0, max: 100, defaultValue: 50 },
      { type: 'input', label: 'Username', placeholder: 'Enter your username' }
    ],
    [
      { type: 'slider', label: 'Brightness', min: 0, max: 100, defaultValue: 75 },
      { type: 'input', label: 'Email', placeholder: 'Enter your email' }
    ],
    [
      { type: 'slider', label: 'Contrast', min: 0, max: 100, defaultValue: 25 },
      { type: 'input', label: 'Password', placeholder: 'Enter your password' }
    ]
  ];

  // Method to change the menuIndex
  changeMenu = (index) => {
    this.setState({ menuIndex: index });
  };

  // Toggle sidenav open/close
  toggleNav = () => {
    this.setState(prevState => ({ isOpen: !prevState.isOpen }));
  };

  renderComponent(item) {
    switch (item.type) {
      case 'slider':
        return (
          <div key={item.label}>
            <label>{item.label}</label>
            <input
              type="range"
              min={item.min}
              max={item.max}
              defaultValue={item.defaultValue}
            />
          </div>
        );
      case 'input':
        return (
          <div key={item.label}>
            <label>{item.label}</label>
            <input type="text" placeholder={item.placeholder} />
          </div>
        );
      default:
        return null;
    }
  }

  render() {
    const { menuIndex, isOpen } = this.state;
    const currentMenu = this.menuConfigs[menuIndex];

    return (
      <div>
        <div id="mySidenav" className={`sidenav ${isOpen ? 'open' : ''}`}>
          <a href="javascript:void(0)" className="closebtn" onClick={this.toggleNav}>
            &times;
          </a>
          <a href="#" onClick={() => this.changeMenu(0)}>Save/Load</a>
          <a href="#" onClick={() => this.changeMenu(1)}>Components</a>
          <a href="#" onClick={() => this.changeMenu(2)}>Assets</a>
          <a href="#">Add/ Remove Databases</a>
          <a href="#">Graphics/ Animations</a>
          <a href="#">Analytics</a>
          <a href="#">Sign Out</a>
        </div>

        <div>
          <button onClick={this.toggleNav}>Open Menu</button>
        </div>

        <div>
          {currentMenu.map(item => this.renderComponent(item))}
        </div>
      </div>
    );
  }
}


const SideNav = () => {
  const [isOpen, setIsOpen] = useState(false);

  const openNav = () => {
    setIsOpen(true);
  };

  const closeNav = () => {
    setIsOpen(false);
  };

  return (
    <>
      <div id="mySidenav" className={`sidenav ${isOpen ? 'open' : ''}`}>
        <a href="javascript:void(0)" className="closebtn" onClick={closeNav}>
          &times;
        </a>
        <a href="#">Save/Load</a>
        <a href="#">Components</a>
        <a href="#">Assets</a>
        <a href="#">Add/ Remove Databases</a>
        <a href="#">Graphics/ Animations</a>
        <a href="#">Analytics</a>
        <a href="#">Sign Out</a>
      </div>
      <span style={{ fontSize: 30, cursor: 'pointer' }} onClick={openNav}>
        &#9776; 
      </span>
      <style>
        {`
          body {
            font-family: "Lato", sans-serif;
          }

          .sidenav {
            height: 100%;
            width: 0;
            position: fixed;
            z-index: 1;
            top: 0;
            left: 0;
            background-color: #111;
            overflow-x: hidden;
            transition: 0.5s;
            padding-top: 60px;
            text-align: center;
          }

          .sidenav.open {
            width: 20%;
          }

          .sidenav a {
            padding: 8px 8px 8px 32px;
            text-decoration: none;
            font-size: 25px;
            color: #818181;
            display: block;
            transition: 0.3s;
          }

          .sidenav a:hover {
            color: #f1f1f1;
          }

          .sidenav .closebtn {
            position: absolute;
            top: 0;
            right: 25px;
            font-size: 36px;
            margin-left: 50px;
          }

          @media screen and (max-height: 450px) {
            .sidenav {
              padding-top: 15px;
            }

            .sidenav a {
              font-size: 18px;
            }
          }
        `}
      </style>
    </>
  );
};

export default SideNav;
