import React,{Component, useState} from 'react';
import {Rnd} from 'react-rnd';
import axios from 'axios';
import TextList from './builder/TextList';
import g from 'react-scale-text';
import NavBar from './components/NavBar';
import Blocks from './blocks/blocks'
import { BrowserRouter as Router, Routes, Route, Link} from 'react-router-dom';
import ImageList from './builder/ImageList'
import SvgList from './builder/SvgList'
import PixabaySearch from './assets/Asset-Load';
import MainMenu from './menu/MainMenu';

import Databases from './databases/Databases';
import FileLoader from './menu/FileLoader';
import Metrics from './metrics/Metrics';

export default function App() {
  const [iframeSrc, setIframeSrc] = useState('http://localhost:8000');

  const [showMetrics, setShowMetrics] = useState(false); // State to track if Metrics is clicked

  // Function to handle Metrics click
  const handleMetricsClick = () => {
      setShowMetrics(true);
  };

  // Function to handle FileLoader click
  const handleFileLoaderClick = () => {
      setShowMetrics(false);
  };
  return (
    <div className="app-container">
        {/* <TextList/> */}
        <Router>
            <MainMenu />
            <FileLoader setIframeSrc={'http://localhost:8000'}/>
            <Routes>
                <Route path="/databases" element={<Databases />} />

                <Route path="/metrics" element={<Metrics />} />

            </Routes>
        </Router>
    </div>
  );
}
