import React from 'react';

class CustomMenu extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      menuIndex: 0 // Default menu index
    };
  }

  // Different menu configurations based on the menuIndex
  menuConfigs = [
    [
      { type: 'slider', label: 'Volume', min: 0, max: 100, defaultValue: 50 },
      { type: 'input', label: 'Username', placeholder: 'Enter your username' }
    ],
    [
      { type: 'slider', label: 'Brightness', min: 0, max: 100, defaultValue: 75 },
      { type: 'input', label: 'Email', placeholder: 'Enter your email' }
    ],
    [
      { type: 'slider', label: 'Contrast', min: 0, max: 100, defaultValue: 25 },
      { type: 'input', label: 'Password', placeholder: 'Enter your password' }
    ]
  ];

  // Method to change the menuIndex
  changeMenu = (index) => {
    this.setState({ menuIndex: index });
    // Close the sidenav after selecting an option
    this.props.closeNav();
  };

  renderComponent(item) {
    switch (item.type) {
      case 'slider':
        return (
          <div key={item.label}>
            <label>{item.label}</label>
            <input
              type="range"
              min={item.min}
              max={item.max}
              defaultValue={item.defaultValue}
            />
          </div>
        );
      case 'input':
        return (
          <div key={item.label}>
            <label>{item.label}</label>
            <input type="text" placeholder={item.placeholder} />
          </div>
        );
      default:
        return null;
    }
  }

  render() {
    const { menuIndex } = this.state;
    const { isOpen, closeNav } = this.props;
    const currentMenu = this.menuConfigs[menuIndex];

    return (
      <div>
        <div id="mySidenav" className={`sidenav ${isOpen ? 'open' : ''}`}>
          <a href="#" className="closebtn" onClick={closeNav}>
            &times;
          </a>
          <a href="#" onClick={() => this.changeMenu(0)}>Save/Load</a>
          <a href="#" onClick={() => this.changeMenu(1)}>Components</a>
          <a href="#" onClick={() => this.changeMenu(2)}>Assets</a>
          <a href="#">Add/ Remove Databases</a>
          <a href="#">Graphics/ Animations</a>
          <a href="#">Analytics</a>
          <a href="#">Sign Out</a>
        </div>

        <div>
          {currentMenu.map(item => this.renderComponent(item))}
        </div>
      </div>
    );
  }
}

export default CustomMenu;
