import React, { Component, useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import {
    Navbar,
    NavbarBrand,
    NavbarToggler,
    Collapse,
    Nav,
    NavItem,
    NavLink,
    Container,
    Dropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem
} from 'reactstrap';
import { Link } from 'react-router-dom';
import SideNav from './SideNav';
import './NavMenu.css';





export default class MainMenu extends Component {
    constructor(props) {
        super(props);
        this.toggleNavbar = this.toggleNavbar.bind(this);
        this.state = {
            collapsed: true
        };
    }

    toggleNavbar() {
        this.setState({
            collapsed: !this.state.collapsed
        });
    }

    render() {
        return (
            <header>
                 
                <Navbar className="navbar-expand-sm navbar-toggleable-sm bg-white border-bottom box-shadow mb-3" light>
                    <Container>
                        <NavbarBrand href="/">KRDC (&copy; 2023)</NavbarBrand>
                      
                        <NavbarToggler onClick={this.toggleNavbar} className="mr-2" />
                        <Collapse className="d-sm-inline-flex flex-sm-row-reverse" isOpen={!this.state.collapsed} navbar>
                        
                            <Nav navbar>
                                
                            <SideNav/>
                            
                                <NavItem>
                                    <NavLink href="/" className="text-dark">Home</NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink href="/" className="text-dark">Dashboard</NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink href="/" className="text-dark">Database</NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink href="/" className="text-dark">CRM</NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink className="text-dark">Subscription/ Key Info</NavLink>
                                    {/* <NavLink tag={Link} to="../blocks/load" className="text-dark">Layout</NavLink> */}
                                </NavItem>
                                <NavItem>
                                    <NavLink href="/" className="text-dark">Help</NavLink>
                                </NavItem>
                                <Dropdown nav isOpen={dropdownOpen} toggle={toggleDropdown}>
                            <DropdownToggle nav caret className="text-dark">
                                Businesses
                            </DropdownToggle>
                            <DropdownMenu>
                                <DropdownItem href="#business1">Business 1</DropdownItem>
                                <DropdownItem href="#business2">Business 2</DropdownItem>
                                <DropdownItem href="#business3">Business 3</DropdownItem>
                                {/* Add more businesses as needed */}
                            </DropdownMenu>
                        </Dropdown>
                            </Nav>
                        
                        </Collapse>
                    </Container>
                </Navbar>
                
                    
{/* 
                <Switch>
                <Route path="../blocks/blocks" component={Blocks} />
                <Route path="../blocks/load" component={Blocks} />
                 </Switch> */}
            </header>
        );
    }
}
