import React, { useState } from 'react';

function Blocks() {
  const [flexBoxes, setFlexBoxes] = useState([]);
  const [gridWidth, setGridWidth] = useState(0);

  const addFlexBox = () => {
    setFlexBoxes([...flexBoxes, { height: 200, gridWidth: 0 }]);
  };

  const handleGridWidthChange = (index, e) => {
    const newFlexBoxes = [...flexBoxes];
    newFlexBoxes[index].gridWidth = parseInt(e.target.value);
    setFlexBoxes(newFlexBoxes);
  };

  return (
    <div>
      <button onClick={addFlexBox}>Add Flex Box</button>
      {flexBoxes.map((box, index) => (
        <div key={index} style={{ display: 'flex', flexDirection: 'column', marginBottom: 10 }}>
          <div style={{ height: box.height, border: '1px solid black', marginBottom: 5 }}>
            Flex Box Height: {box.height}px
          </div>
          <div>
            <label>
              Grid Width:
              <input
                type="number"
                value={box.gridWidth}
                onChange={(e) => handleGridWidthChange(index, e)}
              />
            </label>
          </div>
          {box.gridWidth > 0 && (
            <div style={{ display: 'grid', gridTemplateColumns: `repeat(auto-fit, ${box.gridWidth}px)`, gap: 10 }}>
              {/* Your grid items go here */}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default Blocks;
