import React, { useState } from 'react';

const SideNav = () => {
  const initialMenu = [
    { label: 'Save/Load', submenu: null },
    { label: 'Components', submenu: null },
    { label: 'Assets', submenu: null },
    { label: 'Add/ Remove Databases', submenu: null },
    { label: 'Graphics/ Animations', submenu: null },
    { label: 'Analytics', submenu: null },
    { label: 'Sign Out', submenu: null }
  ];

  const [menuStack, setMenuStack] = useState([]);
  const [currentMenu, setCurrentMenu] = useState(initialMenu);

  const openNav = () => {
    setMenuStack([...menuStack, currentMenu]);
  };

  const closeNav = () => {
    setMenuStack([]);
    setCurrentMenu(initialMenu);
  };

  const goToSubMenu = (submenu) => {
    setMenuStack([...menuStack, currentMenu]);
    setCurrentMenu(submenu);
  };

  const goBack = () => {
    if (menuStack.length > 0) {
      const prevMenu = menuStack.pop();
      setMenuStack([...menuStack]);
      setCurrentMenu(prevMenu);
    }
  };

  const renderMenuItems = () => {
    if (!currentMenu) return null; // Handle case where currentMenu is null or undefined

    return currentMenu.map((item, index) => (
      <a key={index} href={item.submenu ? '#' : '#'} onClick={() => goToSubMenu(item.submenu)}>
        {item.label}
      </a>
    ));
  };

  return (
    <>
      <div id="mySidenav" className={`sidenav ${menuStack.length > 0 ? 'open' : ''}`}>
        <a href="javascript:void(0)" className="closebtn" onClick={closeNav}>
          &times;
        </a>
        {menuStack.length > 0 &&
          <a href="javascript:void(0)" className="backbtn" onClick={goBack}>
            &lt;
          </a>
        }
        {renderMenuItems()}
      </div>
      <span style={{ fontSize: 30, cursor: 'pointer' }} onClick={openNav}>
        &#9776; 
      </span>
      <style>
        {`
          body {
            font-family: "Lato", sans-serif;
          }

          .sidenav {
            height: 100%;
            width: 0;
            position: fixed;
            z-index: 9999;
            top: 0;
            left: 0;
            background-color: #111;
            overflow-x: hidden;
            transition: 0.5s;
            padding-top: 60px;
            text-align: center;
          }

          .sidenav.open {
            width: 20%;
          }

          .sidenav a {
            padding: 8px 8px 8px 32px;
            text-decoration: none;
            font-size: 25px;
            color: #818181;
            display: block;
            transition: 0.3s;
          }

          .sidenav a:hover {
            color: #f1f1f1;
          }

          .sidenav .closebtn {
            position: absolute;
            top: 0;
            right: 25px;
            font-size: 36px;
            margin-left: 50px;
          }

          .sidenav .backbtn {
            position: absolute;
            top: 0;
            left: 25px;
            font-size: 36px;
            margin-right: 50px;
          }

          @media screen and (max-height: 450px) {
            .sidenav {
              padding-top: 15px;
            }

            .sidenav a {
              font-size: 18px;
            }
          }
        `}
      </style>
    </>
  );
};

export default SideNav;





