import React from 'react';

class CustomMenu extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      menuIndex: 0, // Default menu index
    };
  }

  // Method to change the menuIndex
  changeMenu = (index) => {
    this.setState({ menuIndex: index });
    // Close the sidenav after selecting an option
    if (this.props.closeNav) this.props.closeNav();
  };

  renderComponent(item) {
    switch (item.type) {
      case 'slider':
        return (
          <div key={item.label}>
            <label>{item.label}</label>
            <input
              type="range"
              min={item.min}
              max={item.max}
              defaultValue={item.defaultValue}
            />
          </div>
        );
      case 'input':
        return (
          <div key={item.label}>
            <label>{item.label}</label>
            <input type="text" placeholder={item.placeholder} />
          </div>
        );
      default:
        return null;
    }
  }

  render() {
    const { customMenu, isOpen, closeNav } = this.props;
    const { menuIndex } = this.state;
    const currentMenu = customMenu.items[menuIndex] || [];

    return (
      <div>
        <div id="mySidenav" className={`sidenav ${isOpen ? 'open' : ''}`}>
          <a href="#" className="closebtn" onClick={closeNav}>
            &times;
          </a>
          {customMenu.links.map((link, index) => (
            <a href="#" key={index} onClick={() => this.changeMenu(link.index)}>
              {link.label}
            </a>
          ))}
          {/* Render the current menu inside the sidenav */}
          <div className="menu-items">
            {currentMenu.map(item => this.renderComponent(item))}
          </div>
        </div>
      </div>
    );
  }
}

export default CustomMenu;
