// App.js
import React, { useState, useEffect, useRef } from 'react';
import MainMenu from './menu/MainMenu';
import Databases from './databases/Databases';
import FileLoader from './menu/FileLoader';
import Metrics from './metrics/Metrics';
import TextList from './builder/TextList';
import BuilderTable from './BuilderTable';

function App() {
    const [showDatabases, setShowDatabases] = useState(false);
    const [showFileLoader, setShowFileLoader] = useState(false);
    const [showCRM,setShowCRM] = useState(false);
    const [showMetrics, setShowMetrics] = useState(false);
    const [showTextList, setShowTextList] = useState(false); // State for TextList visibility
    const [iframeSrc, setIframeSrc] = useState('http://localhost:3000/output.html');

    // Callback to update showTextList in App
    const handleSetShowTextList = (value) => {
        setShowTextList(value);
    };

    // Handlers to show specific components
    const handleShowDatabases = () => {
        setShowDatabases(true);
        setShowFileLoader(false);
        setShowMetrics(false);
        setShowCRM(false);
    };

    const handleShowFileLoader = () => {
        setShowDatabases(false);
        setShowFileLoader(true);
        setShowMetrics(false);
        setShowCRM(true);
    };

    const handleShowCRM = () => {
        setShowDatabases(false);
        setShowFileLoader(true);
        setShowMetrics(false);
        setShowCRM(true);
    };

    const handleShowMetrics = () => {
        setShowDatabases(false);
        setShowFileLoader(false);
        setShowMetrics(true);
        setShowCRM(false);
    };

    useEffect(() => {
        // Add an event listener for messages from the iframe
        const handleMessage = (event) => {
            // Ensure the message is from a trusted source
            // Replace '*' with a specific domain for security in production
            const { data } = event;
            if (data && data.dataArrId) {
                console.log('Received data-arr-id from iframe:', data.dataArrId);
                // Handle the data-arr-id as needed
            }
        };

        window.addEventListener('message', handleMessage);

        // Clean up the event listener on component unmount
        return () => {
            window.removeEventListener('message', handleMessage);
        };
    }, []);

    return (
        <div>
            <MainMenu
                onShowDatabases={handleShowDatabases}
                onShowFileLoader={handleShowFileLoader}
                onShowMetrics={handleShowMetrics}
                onShowCRM={handleShowCRM}
            />
            <div className="app-container">
                {showDatabases && <Databases />}
                {showMetrics && <Metrics />}
                {showFileLoader && (
                    <FileLoader
                        setIframeSrc={setIframeSrc}
                        iframeSrc={iframeSrc}
                        onSetShowTextList={handleSetShowTextList} 
                    />
                )}
                {/* {showTextList && <TextList />}  */}


            </div>
            <div data-arr-id="1U6hlV6C-b">Done</div>
        </div>
    );
}

export default App;
