// MainMenu.js
import React, { useState } from 'react';
import { Nav, NavItem, NavLink, Navbar, NavbarBrand, NavbarToggler, Collapse, Container, Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
import { Link } from 'react-router-dom';
import SideNav from './SideNav';
import CustomMenu from './CustomMenu';
import './NavMenu.css';

const MainMenu = ({ onShowDatabases, onShowFileLoader, onShowMetrics }) => {
    const [collapsed, setCollapsed] = useState(true);
    const [dropdownOpen, setDropdownOpen] = useState(false);

    const toggleNavbar = () => setCollapsed(!collapsed);
    const toggleDropdown = () => setDropdownOpen(!dropdownOpen);

    return (
        <Navbar className="navbar-expand-sm navbar-toggleable-sm bg-white border-bottom box-shadow mb-3" light>
            <Container>
                <NavbarBrand href="/">KRDC (&copy; 2023)</NavbarBrand>
                <NavbarToggler onClick={toggleNavbar} className="mr-2" />
                <Collapse className="d-sm-inline-flex flex-sm-row-reverse" isOpen={!collapsed} navbar>
                    <Nav navbar>
                        <SideNav customMenu={<CustomMenu />} />
                        <NavItem>
                            <NavLink href="#" onClick={onShowDatabases} className="text-dark">Database</NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink href="#" onClick={onShowFileLoader} className="text-dark">File Loader</NavLink>
                        </NavItem>
                        <NavItem>
                            <NavLink href="#" onClick={onShowMetrics} className="text-dark">Metrics</NavLink>
                        </NavItem>
                        {/* Add other menu items as needed */}
                    </Nav>
                </Collapse>
            </Container>
        </Navbar>
    );
};

export default MainMenu;
