import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useLocation } from 'react-router-dom';
import PixabaySearch from '../assets/Asset-Load';

const SideNav = () => {
  // Initial menu items
  const initialMenu = [
    { label: 'Save/Load', route: '/save-load' },
    { label: 'Components', route: '/components' },
    { label: 'Assets', route: '/assets' },
    { label: 'Add/ Remove Databases', route: '/databases' },
    { label: 'Graphics/ Animations', route: '/graphics' },
    { label: 'Analytics', route: '/analytics' },
    { label: 'Sign Out', route: '/sign-out' }
  ];

  // State variables
  const [menuStack, setMenuStack] = useState([]);
  const [currentMenu, setCurrentMenu] = useState(initialMenu);

  // Function to open side navigation menu
  const openNav = () => {
    setMenuStack([...menuStack, currentMenu]);
  };

  // Function to close side navigation menu
  const closeNav = () => {
    setMenuStack([]);
    setCurrentMenu(initialMenu);
  };

  // Function to navigate to a submenu
  const goToSubMenu = (submenu) => {
    setMenuStack([...menuStack, currentMenu]);
    setCurrentMenu(submenu);
  };

  // Function to go back to the previous menu level
  const goBack = () => {
    if (menuStack.length > 0) {
      const prevMenu = menuStack.pop();
      setMenuStack([...menuStack]);
      setCurrentMenu(prevMenu);
    }
  };

  // Render menu items based on currentMenu state
  const renderMenuItems = () => {
    if (!currentMenu) return null; // Handle case where currentMenu is null or undefined

    return currentMenu.map((item, index) => (
      <Link key={index} to={item.route} onClick={closeNav}>
        {item.label}
      </Link>
    ));
  };

  return (
    <Router>
      <div id="mySidenav" className={`sidenav ${menuStack.length > 0 ? 'open' : ''}`}>
        <a href="javascript:void(0)" className="closebtn" onClick={closeNav}>
          &times;
        </a>
        {menuStack.length > 0 && (
          <a href="javascript:void(0)" className="backbtn" onClick={goBack}>
            &lt;
          </a>
        )}
        {renderMenuItems()}
      </div>
      <span style={{ fontSize: 30, cursor: 'pointer' }} onClick={openNav}>
        &#9776; 
      </span>
      <Routes>
        {initialMenu.map((item, index) => (
          <Route key={index} path={item.route}>
            {/* Render components or content for each route */}
            {item.route === '/save-load' && <PixabaySearch />}
            {/* Add other components as needed */}
          </Route>
        ))}
      </Routes>
      <style>
        {`
          /* Your CSS styles for the side navigation */
        `}
      </style>
    </Router>
  );
};

export default SideNav;




