function RemoveElement() {
    useEffect(() => {
      const handleTripleClick = (event) => {
        const element = event.target;
        const arId = element.getAttribute('data-ar-id');
  
        if (arId) {
          console.log(`Triple-click detected on element with data-ar-id: ${arId}`);
          removeElementFromServer(arId);
        }
      };
  
      // Attach the event listener
      document.addEventListener('click', handleTripleClick);
  
      return () => {
        // Cleanup the event listener
        document.removeEventListener('click', handleTripleClick);
      };
    }, []);
  
    const removeElementFromServer = (arId) => {
      axios.post('/remove-element', { id: arId })
        .then(response => {
          console.log('Element removed:', response.data);
        })
        .catch(error => {
          console.error('Error removing element:', error);
        });
    };
  
    return (
      <div>
        <h3>Triple-click any element with a data-ar-id attribute to remove it.</h3>
      </div>
    );
  }
  
  export default RemoveElement;
