//import zIndex from "@material-ui/core/styles/zIndex";
import FontSelect from '../fonts/font-select';
import { relative } from "path";



const fs = require('fs');

class TextList extends React.Component {


  
  constructor(props){
    super(props);
   
    this.state = {
      item: [],
      borders: [],
      isMenuVisible: false,
      
      }
      this.liContainers = [];
    }
 
  

  // componentDidMount() {
  //   axios.get('http://localhost:9000/reactive-website-builder/APIs/select-text-data.php')
  //     .then(res => {
