          //            contentEditable={!item}
            //          className={this.state.borders[index] ? 'border' : ''}
              //      ></li>
//onMinusClick={() => this.handleQuantityChange(item.id, -1)} onPlusClick={() => this.handleQuantityChange(item.id, 1)} quantity={item.quantity}

render() {
  const itemlist = this.state.item;
  const rndStyle = {
    display: 'flex',
    // alignItems: 'center',
    // justifyContent: 'center',
    // border: '1px solid #ddd',
    // background: '#f0f0f0',
  };
  if (this.state.item.length === 0) {
    return null;
  }

  var top = 248;
  var left = 700;

  return (
    <div style={{ position: 'absolute', left: 0, right: 0, bottom: 0, top: 0, overflow: 'auto' }}>
      <ul>
        {itemlist.map((record, index) => {
          console.log(`record ${index}.isOpen: ${record.isOpen}`);
          console.log(`record ${index}.isResizable: ${record.isOpen ? 'true' : 'false'}`);
          return (
            <Rnd
              key={index}
              bounds=".app-container"
              style={rndStyle}
              minWidth={this.liContainers[index].current?.clientWidth}
              minHeight={this.liContainers[index].current?.clientHeight}
             
              
              onDragStop={record.isOpen ? this.eventLogger : null}
              
              resizable={record.isOpen ? {
                edges: {
                  top: true,
                  right: true,
                  bottom: true,
                  left: true,
                },
              } : false}
              resizeHandleWrapperStyle={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
              }}
              
            >
              <li
                
                ref={this.liContainers[index]}
                key={index}
                
                style={{
                  fontSize: itemlist[index].fontsize,
                  fontFamily: itemlist[index].font,
                  color: itemlist[index].color,
                  width: "100%",
              
                  padding: "10px",
                  boxSizing: "border-box",
                  position: "relative"
                }}
                onDoubleClick={() => {
                  this.toggleMenu(index);
                  this.toggleEditable(index);
                }}
                contentEditable={itemlist[index].isEditable}
              >
                {ReactHtmlParser(record.itemcode)}

                <div style={{ position: "relative", zIndex: 3 }}>
                  <Menu
                    handleTextGrow={() => this.handleTextGrow(index)}
                    handleTextShrink={() => this.handleTextShrink(index)}
                    handleFontChange={(event) =>
                      this.handleFontChange(event, index)
                    }
                    handleListResize={this.handleListResize}
                    handleColorChange={(event) =>
                      this.handleColorChange(event, index)
                    }
                    isOpen={record.isOpen}
                    style={{ border: "none", zIndex: 100 }}
                    contentEditable={false}
                  />
                </div>
              </li>

              <div></div>
            </Rnd>
          );
        })}
      </ul>
    </div>
  );
}
}

   
