import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import PixabaySearch from './assets/Asset-Load';
import MainMenu from './menu/MainMenu';

import './App.css';

function App() {
  // State to trigger route change effect
  const [key, setKey] = useState(0);

  // Effect to handle route change and trigger reload
  useEffect(() => {
    const handleRouteChange = () => {
      setKey(prevKey => prevKey + 1); // Update key to force remount of Routes
    };

    window.addEventListener('popstate', handleRouteChange); // Listen for route changes
    return () => {
      window.removeEventListener('popstate', handleRouteChange); // Clean up event listener
    };
  }, []);

  return (
    <Router key={key}> {/* Key prop to remount Router and its children on key change */}
      <div className="App">
        <MainMenu />
        <div className="app-container">
          {/* Routes */}
          <Routes>
            <Route path="/save-load" element={<h1>Save/Load Page</h1>} />
            <Route path="/components" element={<h1>Components Page</h1>} />
            <Route path="/assets" element={<PixabaySearch />} />
            <Route path="/databases" element={<h1>Databases Page</h1>} />
            <Route path="/graphics" element={<h1>Graphics Page</h1>} />
            <Route path="/analytics" element={<h1>Analytics Page</h1>} />
            <Route path="/sign-out" element={<h1>Sign Out Page</h1>} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
