// App.js
import React, { useState, useRef, useEffect } from 'react';
import MainMenu from './menu/MainMenu';
import Databases from './databases/Databases';
import FileLoader from './menu/FileLoader';
import Metrics from './metrics/Metrics';
import TextList from './builder/TextList';
import BuilderTable from './BuilderTable';
import fs from 'fs';
import cheerio from 'cheerio';

function App() {
    const [showDatabases, setShowDatabases] = useState(false);
    const [showFileLoader, setShowFileLoader] = useState(false);
    const [showMetrics, setShowMetrics] = useState(false);
    const [iframeSrc, setIframeSrc] = useState('http://localhost:3000/output.html');
    const elementRefs = useRef([]);
    const handleShowDatabases = () => {
        setShowDatabases(true);
        setShowFileLoader(false);
        setShowMetrics(false);
    };

    const handleShowFileLoader = () => {
        setShowDatabases(false);
        setShowFileLoader(true);
        setShowMetrics(false);
    };

    const handleShowMetrics = () => {
        setShowDatabases(false);
        setShowFileLoader(false);
        setShowMetrics(true);
    };


    useEffect(() => {
        const handleTripleClick = (event) => {
            const element = event.target;
            const arId = element.getAttribute('data-ar-id');

            if (arId) {
                removeElementFromHTML(arId);
            }
        };

        // Attach event listeners
        elementRefs.current.forEach(ref => {
            if (ref) {
                ref.addEventListener('click', handleTripleClick);
            }
        });

        return () => {
            // Cleanup event listeners
            elementRefs.current.forEach(ref => {
                if (ref) {
                    ref.removeEventListener('click', handleTripleClick);
                }
            });
        };
    }, []);

    const removeElementFromHTML = (arId) => {
        fs.readFile('path/to/your/file.html', 'utf-8', (err, data) => {
            if (err) {
                console.error('Error reading file:', err);
                return;
            }

            const $ = cheerio.load(data);
            $(`[data-ar-id='${arId}']`).remove();

            fs.writeFile('path/to/your/file.html', $.html(), (err) => {
                if (err) {
                    console.error('Error writing to file:', err);
                } else {
                    console.log('Element removed and file saved successfully!');
                }
            });
        });
    };

    return (
        <div>
            <MainMenu
                onShowDatabases={handleShowDatabases}
                onShowFileLoader={handleShowFileLoader}
                onShowMetrics={handleShowMetrics}
            />
            <div className="app-container">
                {showDatabases && <Databases />}
                {showMetrics && <Metrics />}
                {showFileLoader && <FileLoader setIframeSrc={setIframeSrc} iframeSrc={iframeSrc} />}
            </div>
        </div>
    );
};

export default App;
