import React,{Component, useState, useEffect} from 'react';
import {Rnd} from 'react-rnd';
import axios from 'axios';
import TextList from './builder/TextList';
import g from 'react-scale-text';
import NavBar from './components/NavBar';
import Blocks from './blocks/blocks'
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import ImageList from './builder/ImageList'
import SvgList from './builder/SvgList'
import PixabaySearch from './assets/Asset-Load';

import './App.css';

import MainMenu from './menu/MainMenu';

import './App.css';


function App() {



  function RouteChangeReload({ children }) {
    useEffect(() => {
      const handleRouteChange = () => {
        window.location.reload();
      };

      // Listen for route changes and reload the page
      window.addEventListener('popstate', handleRouteChange);
      return () => {
        window.removeEventListener('popstate', handleRouteChange);
      };
    }, []);

    return children;
  }



  return (

      <div className="App">
        <MainMenu />
        <div className="app-container">
          {/* Routes */}
          <Router>
          <RouteChangeReload>
            <Routes>
              <Route path="/save-load" element={<h1>Save/Load Page</h1>} />
              <Route path="/components" element={<h1>Components Page</h1>} />
              <Route path="/assets" element={<PixabaySearch />} />
              <Route path="/databases" element={<h1>Databases Page</h1>} />
              <Route path="/graphics" element={<h1>Graphics Page</h1>} />
              <Route path="/analytics" element={<h1>Analytics Page</h1>} />
              <Route path="/sign-out" element={<h1>Sign Out Page</h1>} />
            </Routes>
          </RouteChangeReload>
          </Router>
        </div>
      </div>

  );
}

export default App;
