import React, { Component, useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import { Collapse, Container, Navbar, NavbarBrand, NavbarToggler, NavItem, NavLink, Nav } from 'reactstrap';
import { Link } from 'react-router-dom';
import SideNav from './SideNav';
import './NavMenu.css';





const FileLoader = () => {
    // URLs stored in variables
    const indexHtml1Url = 'http://localhost:7001';
    const indexHtml2Url = 'http://localhost:7000';
    const indexPhpUrl = 'http://localhost:8000/admin';
  
    // State to manage the current URL for the iframe
    const [iframeSrc, setIframeSrc] = useState(indexPhpUrl);
  
    return (
      <div>
        <button onClick={() => setIframeSrc(indexPhpUrl)}>
          Web Editor
        </button>
        <button onClick={() => setIframeSrc(indexHtml1Url)}>
          Photo Editor
        </button>
        <button onClick={() => setIframeSrc(indexHtml2Url)}>
          Video Editor
        </button>
        <button onClick={() => setIframeSrc(indexHtml2Url)}>
          Graphics Engine
        </button>
    
        <iframe
          src={iframeSrc}
          style={{ width: '100%', height: '800px', border: 'none' }}
          title="Content Viewer"
        />
      </div>
    ); 
  };
  

  export default FileLoader;
