import React, { Component, useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import { Collapse, Container, Navbar, NavbarBrand, NavbarToggler, NavItem, NavLink, Nav } from 'reactstrap';
import { Link } from 'react-router-dom';
import SideNav from './SideNav';
import './NavMenu.css';
import axios from 'axios';

import RemoveElement from './transfer-edits/RemoveElement';


const FileLoader = ({ iframeSrc, setIframeSrc }) => {
    const [iframeLoaded, setIframeLoaded] = useState(false);
    const iframeRef = React.createRef();

    // Define URLs for different editors
    const indexHtml1Url = 'http://localhost:7001';
    const indexHtml2Url = 'http://localhost:7000';
    const indexPhpUrl = 'http://localhost:8000';

    useEffect(() => {
        if (iframeLoaded) {
            const iframeDoc = iframeRef.current.contentWindow.document;
            const handleTripleClick = (event) => {
                const element = event.target;
                const arId = element.getAttribute('data-ar-id');

                if (arId) {
                    console.log(`Triple-click detected on element with data-ar-id: ${arId}`);
                    removeElementFromServer(arId);
                }
            };

            // Attach the event listener to iframe document
            iframeDoc.addEventListener('click', handleTripleClick);

            // Cleanup event listener when iframe content changes
            return () => {
                iframeDoc.removeEventListener('click', handleTripleClick);
            };
        }
    }, [iframeLoaded]);

    const removeElementFromServer = (arId) => {
        axios.post('/remove-element', { id: arId })
            .then(response => {
                console.log('Element removed:', response.data);
            })
            .catch(error => {
                console.error('Error removing element:', error);
            });
    };

    const handleIframeLoad = () => {
        setIframeLoaded(true);
    };

    return (
        <div>
            <button onClick={() => setIframeSrc(indexPhpUrl)}>
                Web Editor
            </button>
            <button onClick={() => setIframeSrc(indexHtml1Url)}>
                Photo Editor
            </button>
            <button onClick={() => setIframeSrc(indexHtml2Url)}>
                Video Editor
            </button>
            <button onClick={() => setIframeSrc(indexHtml2Url)}>
                Graphics Engine
            </button>

            <iframe
                ref={iframeRef}
                src={iframeSrc}
                style={{ width: '100%', height: '800px', border: 'none' }}
                title="Content Viewer"
                onLoad={handleIframeLoad}
            />
        </div>
    );
};

export default FileLoader;
