import React, { Component, useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import { Collapse, Container, Navbar, NavbarBrand, NavbarToggler, NavItem, NavLink, Nav } from 'reactstrap';
import { Link } from 'react-router-dom';
import SideNav from './SideNav';
import './NavMenu.css';


const FileLoader = () => {
    // URLs stored in variables
    const indexHtml1Url = 'http://localhost:6000';
    const indexHtml2Url = 'http://localhost:7000';
    const indexPhpUrl = 'http://localhost:7001';
  
    // State to manage the current URL for the iframe
    const [iframeSrc, setIframeSrc] = useState(indexHtml1Url);
  
    return (
      <div>
        <button onClick={() => setIframeSrc(indexPhpUrl)}>
          Web Editor
        </button>
        <button onClick={() => setIframeSrc(indexHtml1Url)}>
          Photo Editor
        </button>
        <button onClick={() => setIframeSrc(indexHtml2Url)}>
          Video Editor
        </button>
        <button onClick={() => setIframeSrc(indexHtml2Url)}>
          Graphics Engine
        </button>
    
        <iframe
          src={iframeSrc}
          style={{ width: '100%', height: '800px', border: 'none' }}
          title="Content Viewer"
        />
      </div>
    ); 
  };
  


export default class MainMenu extends Component {
    constructor(props) {
        super(props);
        this.toggleNavbar = this.toggleNavbar.bind(this);
        this.state = {
            collapsed: true
        };
    }

    toggleNavbar() {
        this.setState({
            collapsed: !this.state.collapsed
        });
    }

    render() {
        return (
            <header>
                 
                <Navbar className="navbar-expand-sm navbar-toggleable-sm bg-white border-bottom box-shadow mb-3" light>
                    <Container>
                        <NavbarBrand href="/">KRDC (&copy; 2023)</NavbarBrand>
                      
                        <NavbarToggler onClick={this.toggleNavbar} className="mr-2" />
                        <Collapse className="d-sm-inline-flex flex-sm-row-reverse" isOpen={!this.state.collapsed} navbar>
                        
                            <Nav navbar>
                                
                            <SideNav/>
                            
                                <NavItem>
                                    <NavLink href="/" className="text-dark">Home</NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink href="/" className="text-dark">Dashboard</NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink href="/" className="text-dark">Database</NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink href="/" className="text-dark">CRM</NavLink>
                                </NavItem>
                                <NavItem>
                                    <NavLink className="text-dark">Subscription</NavLink>
                                    {/* <NavLink tag={Link} to="../blocks/load" className="text-dark">Layout</NavLink> */}
                                </NavItem>
                                <NavItem>
                                    <NavLink href="/" className="text-dark">Help</NavLink>
                                </NavItem>
                            </Nav>
                        </Collapse>
                    </Container>
                </Navbar>
                <FileLoader/>
                    
{/* 
                <Switch>
                <Route path="../blocks/blocks" component={Blocks} />
                <Route path="../blocks/load" component={Blocks} />
                 </Switch> */}
            </header>
        );
    }
}
