import React, { useState, useEffect } from 'react';
import MainMenu from './menu/MainMenu';
import Databases from './databases/Databases';
import FileLoader from './menu/FileLoader';
import Metrics from './metrics/Metrics';
import TextList from './builder/TextList';
import axios from 'axios';

function App() {
    const [showDatabases, setShowDatabases] = useState(false);
    const [showFileLoader, setShowFileLoader] = useState(false);
    const [showMetrics, setShowMetrics] = useState(false);
    const [showTextList, setShowTextList] = useState(false); // State for TextList visibility
    const [iframeSrc, setIframeSrc] = useState('http://localhost:3000/output.html');

    useEffect(() => {
        let clickCount = 0;
        let clickTimer = null;

        const handleTripleClick = async (event) => {
            clickCount++;

            if (clickCount === 3) {
                clickCount = 0; // Reset the click count
                clearTimeout(clickTimer); // Clear the timer

                const dataArId = event.target.getAttribute('data-ar-id');
                if (dataArId) {
                    console.log('Triple-click detected:', dataArId);

                    // Get the outer HTML of the clicked element
                    const outerHtml = event.target.outerHTML;

                    try {
                        // Call the `/remove-element` API
                        const response = await axios.post('http://localhost:5000/remove-element', {
                            id: dataArId, // Pass the `dataArId` as the request payload
                        });

                        // Handle response from the server
                        if (response.data && response.data.showTextList !== undefined) {
                            console.log(response.data.message); // Log the success message
                            setShowTextList(response.data.showTextList); // Update state if needed
                        }
                    } catch (error) {
                        console.error('Error removing element on server:', error);
                    }
                }

                clearTimeout(clickTimer);
            } else {
                // Reset the click count after a short delay
                clearTimeout(clickTimer);
                clickTimer = setTimeout(() => {
                    clickCount = 0;
                }, 500); // 500ms delay between clicks
            }
        };

        // Listen for messages from iframe
        const handleMessage = async (event) => {
            console.log('Message received in parent app:', event);

            // Check if the message contains the `dataArId`
            if (event.data && event.data.dataArId) {
                const { dataArId } = event.data.data;
                console.log('Received dataArId from iframe:', dataArId);

                try {
                    // Call the `/remove-element` API
                    const response = await axios.post('http://localhost:5000/remove-element', {
                        id: dataArId, // Pass the `dataArId` as the request payload
                    });

                    // Handle response from the server
                    if (response.data && response.data.showTextList !== undefined) {
                        console.log(response.data.message); // Log the success message
                        setShowTextList(response.data.showTextList); // Update state if needed
                    }
                } catch (error) {
                    console.error('Error removing element on server:', error);
                }
            }
        };

        window.addEventListener('message', handleMessage);
        document.addEventListener('click', handleTripleClick);

        return () => {
            document.removeEventListener('click', handleTripleClick);
            window.removeEventListener('message', handleMessage);
            clearTimeout(clickTimer);
        };
    }, []);

    // Handlers to show specific components
    const handleShowDatabases = () => {
        setShowDatabases(true);
        setShowFileLoader(false);
        setShowMetrics(false);
    };

    const handleShowFileLoader = () => {
        setShowDatabases(false);
        setShowFileLoader(true);
        setShowMetrics(false);
    };

    const handleShowMetrics = () => {
        setShowDatabases(false);
        setShowFileLoader(false);
        setShowMetrics(true);
    };

    const handleShowCRM = () => {
        // Set iframeSrc to localhost:8080 when CRM is selected
        setIframeSrc('http://localhost:8080');
        setShowDatabases(false);
        setShowFileLoader(true);
        setShowMetrics(false);
    };

    return (
        <div>
            <MainMenu
                onShowDatabases={handleShowDatabases}
                onShowFileLoader={handleShowFileLoader}
                onShowMetrics={handleShowMetrics}
                onShowCRM={handleShowCRM}
            />
            <div className="app-container">
                {showDatabases && <Databases />}
                {showMetrics && <Metrics />}
                {showFileLoader && (
                    <FileLoader
                        setIframeSrc={setIframeSrc}
                        iframeSrc={iframeSrc}
                        onSetShowTextList={setShowTextList} // Directly set state
                    />
                )}
                {showTextList && <TextList />}
            </div>
        </div>
    );
}

export default App;
